# Numeric Data Types
print("Python has 3 numeric types: int, float, complex")
myValue=1
print(myValue)
print(type(myValue))
print(str(myValue) + " is of data type " + str(type(myValue)))
myValue=3.14
print(myValue)
print(type(myValue))
print(str(myValue) + " is of data type " + str(type(myValue)))
myValue=5j
print(myValue)
print(type(myValue))
print(str(myValue) + " is of data type " + str(type(myValue)))
myValue=True
print(myValue)
print(type(myValue))
print(str(myValue) + " is of data type " + str(type(myValue)))